mapper.select:
SELECT DISTINCT PNO, PRODUCTID, TITLE, LINK, IMAGE, LPRICE, HPRICE, BRAND, CATEGORY2, CATEGORY3 FROM SHOP WHERE CATEGORY2 = '캠핑'
AND TITLE LIKE '%${title}%'
AND LPRICE <= ${hPrice}
AND LPRICE >= ${lPrice}
AND (
CATEGORY3 LIKE '%${item}%'
)
AND (
BRAND LIKE '%${brand}%'
)
AND (
BRAND NOT LIKE '%코베아%' AND BRAND NOT LIKE '%엘바운드%' AND BRAND NOT LIKE '%로티캠프%' AND BRAND NOT LIKE '%크레모아%' AND BRAND NOT LIKE '%브리즈문%' AND BRAND NOT LIKE '%씨투써밋%' AND BRAND NOT LIKE '%네이처하이크%' AND BRAND NOT LIKE '%캥거%' AND BRAND NOT LIKE '%카나모%' AND BRAND NOT LIKE '%헬리녹스%' AND BRAND NOT LIKE '%루아즈%'
)
ORDER BY PNO ASC LIMIT ${limit} OFFSET ${offset}

SELECT COUNT(DISTINCT PNO) FROM SHOP WHERE CATEGORY2 = '캠핑'
AND TITLE LIKE '%${title}%'
AND LPRICE <= ${hPrice}
AND LPRICE >= ${lPrice}
AND (
CATEGORY3 LIKE '%${item}%'
)
AND (
BRAND LIKE '%${brand}%'
)
AND (
BRAND NOT LIKE '%코베아%' AND BRAND NOT LIKE '%엘바운드%' AND BRAND NOT LIKE '%로티캠프%' AND BRAND NOT LIKE '%크레모아%' AND BRAND NOT LIKE '%브리즈문%' AND BRAND NOT LIKE '%씨투써밋%' AND BRAND NOT LIKE '%네이처하이크%' AND BRAND NOT LIKE '%캥거%' AND BRAND NOT LIKE '%카나모%' AND BRAND NOT LIKE '%헬리녹스%' AND BRAND NOT LIKE '%루아즈%'
)
